mapper.select:
select

from workrecord

  1=1

    and wID = #{wID,jdbcType=INTEGER}


    and question like concat('%',#{question,jdbcType=VARCHAR},'%')


    and status = #{status,jdbcType=INTEGER}


    and beginDate >= #{beginDate,jdbcType=TIMESTAMP}


    and endDate <= #{endDate,jdbcType=TIMESTAMP}


    and level = #{level,jdbcType=INTEGER}


    and userID = #{userID,jdbcType=INTEGER}


order by beginDate desc

select
wID
from workrecord

where userID=#{userID,jdbcType=INTEGER}

ORDER BY beginDate desc
limit 0,#{number,jdbcType=INTEGER}

select
count(*)
from workrecord
where status = #{status,jdbcType=INTEGER}

  and userID = #{userID,jdbcType=INTEGER}

mapper.insert:
insert into workrecord (wID, UUID, question, 
  beginDate, endDate, comment, 
  level, status, userID
  )
values (#{wID,jdbcType=INTEGER}, #{UUID,jdbcType=VARCHAR}, #{question,jdbcType=VARCHAR}, 
  #{beginDate,jdbcType=TIMESTAMP}, #{endDate,jdbcType=TIMESTAMP}, #{comment,jdbcType=VARCHAR}, 
  #{level,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, #{userID,jdbcType=INTEGER}
  )
